DROP TABLE IF EXISTS users CASCADE ;
DROP TABLE IF EXISTS movies CASCADE;
DROP TABLE IF EXISTS shows CASCADE;

-- Creation of user table
CREATE TABLE IF NOT EXISTS users (
                                     user_id varchar(250) PRIMARY KEY NOT NULL,
                                     username varchar(250) UNIQUE NOT NULL,
                                     email      VARCHAR(255) UNIQUE NOT NULL,
                                     firstname varchar(250) NULL,
                                     lastname varchar(250) NULL,
                                     password varchar(250) NULL,
                                     credit INT NULL,
                                     created_at TIMESTAMP WITH TIME ZONE DEFAULT NULL,
                                     updated_at TIMESTAMP WITH TIME ZONE DEFAULT NULL,
                                     deleted_at TIMESTAMP WITH TIME ZONE DEFAULT NULL
);



-- Creation of Links table
CREATE TABLE IF NOT EXISTS links (
                                    link_id varchar(250) PRIMARY KEY NOT NULL,
                                    title varchar(250) NULL,
                                    address varchar(250) NULL,
                                    published_by varchar(250) NULL
);



-- Creation of Movies table
CREATE TABLE IF NOT EXISTS movies (
                                   movie_id varchar(250) PRIMARY KEY NOT NULL,
                                   title varchar(250) NULL,
                                   release_date TIMESTAMP WITH TIME ZONE NULL,
                                   box_office varchar(250) NULL,
                                   duration INT NULL,
                                   overview TEXT NULL,
                                   cover_url varchar(250) NULL,
                                   trailer_url  varchar(250) NULL,
                                   directed_by  varchar(250) NULL,
                                   phase  INT NULL,
                                   saga  varchar(250) NULL,
                                   chronology INT NULL,
                                   post_credit_scenes varchar(250) NULL,
                                   imdb_id  varchar(250) NULL,
                                   published_by varchar(250) NOT NULL


);

-- Creation of shows table
CREATE TABLE IF NOT EXISTS shows (
                                    show_id varchar(250) PRIMARY KEY NOT NULL,
                                    title varchar(250) NOT NULL,
                                    release_date TIMESTAMP WITH TIME ZONE NULL,
                                    last_aired_date TIMESTAMP WITH TIME ZONE NULL,
                                    number_seasons INT NULL,
                                    number_episodes INT NULL,
                                    overview TEXT NULL,
                                    cover_url varchar(250) NULL,
                                    trailer_url varchar(250) NULL,
                                    directed_by varchar(250) NULL,
                                    phase varchar(250) NULL,
                                    saga varchar(250) NULL,
                                    imdb_id varchar(250) NULL,
                                    published_by varchar(250) NOT NULL

);



